let currentValue = '0';
let previousValue = '';
let operation = null;
let shouldResetDisplay = false;

const display = document.getElementById('display');

function updateDisplay() {
  display.textContent = currentValue;
}

function handleNumber(num) {
  if (shouldResetDisplay) {
    currentValue = num;
    shouldResetDisplay = false;
  } else {
    currentValue = currentValue === '0' ? num : currentValue + num;
  }
  updateDisplay();
}

function handleOperator(op) {
  if (operation && !shouldResetDisplay) {
    calculate();
  }
  previousValue = currentValue;
  operation = op;
  shouldResetDisplay = true;
}

function calculate() {
  const prev = parseFloat(previousValue);
  const current = parseFloat(currentValue);
  let result;

  switch (operation) {
    case 'add': result = prev + current; break;
    case 'subtract': result = prev - current; break;
    case 'multiply': result = prev * current; break;
    case 'divide': result = prev / current; break;
    default: return;
  }

  currentValue = String(result);
  operation = null;
  shouldResetDisplay = true;
  updateDisplay();
}

document.querySelectorAll('.btn').forEach(btn => {
  btn.addEventListener('click', () => {
    if (btn.dataset.value) {
      handleNumber(btn.dataset.value);
    } else if (btn.dataset.action) {
      switch (btn.dataset.action) {
        case 'clear':
          currentValue = '0';
          previousValue = '';
          operation = null;
          updateDisplay();
          break;
        case 'sign':
          currentValue = String(-parseFloat(currentValue));
          updateDisplay();
          break;
        case 'percent':
          currentValue = String(parseFloat(currentValue) / 100);
          updateDisplay();
          break;
        case 'equals':
          calculate();
          break;
        default:
          handleOperator(btn.dataset.action);
      }
    }
  });
});

// Keyboard support
document.addEventListener('keydown', e => {
  if (e.key >= '0' && e.key <= '9') handleNumber(e.key);
  if (e.key === '.') handleNumber('.');
  if (e.key === '+') handleOperator('add');
  if (e.key === '-') handleOperator('subtract');
  if (e.key === '*') handleOperator('multiply');
  if (e.key === '/') handleOperator('divide');
  if (e.key === 'Enter' || e.key === '=') calculate();
  if (e.key === 'Escape') {
    currentValue = '0';
    previousValue = '';
    operation = null;
    updateDisplay();
  }
});

