function add(a, b) {
  return a + b;
}

console.log(add(5, 3));

function Welcome({ name }) {
  return <h1>Hello, {name}!</h1>;
}

export default Welcome;

import { useState, useEffect } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    console.log('Count changed:', count);
  }, [count]);

  return (
    <button onClick={() => setCount(count + 1)}>
      Count: {count}
    </button>
  );
}

